input.body:
{
    "resourceMetrics": [
        {
            "resource": {
                "attributes": $(.execution.input.resourceAttributes | toEntries | map({ key, value: { stringValue: .value }}) // [])
            },
            "scopeMetrics": [
                {
                    "metrics": [
                        {
                            "name": "$(.execution.input.metricName | toEscapedJson)",
                            "description": "$(.execution.input.message | toEscapedJson)",
                            "gauge": {
                                "dataPoints": [
                                    {
                                        "asDouble": 1,
                                        "attributes": [
                                            {
                                                "key": "sap.calm.hm.metric.type",
                                                "value": {
                                                    "stringValue": "status"
                                                }
                                            },
                                            {
                                                "key": "sap.calm.hm.metric.rating",
                                                "value": {
                                                    "stringValue": "$(.execution.input.status)"
                                                }
                                            }
                                        ]
                                    }
                                ]
                            }
                        }
                    ]
                }
            ]
        }
    ]
}
